import * as React from 'react'
import {Header,TextInput,View,TouchableOpacity,Alert,KeyboardAvoidingView,ToastAndroid} from 'react-native';
import db from '../config'
import firebase from 'firebase'


export default class writeStory extends React.Component{
constructor(){
    super();
    this.state={
        author:'',
        title:'',
        story:''
    }
}

    submitStory(){
    db.collection('user').add({
        author:this.state.author,
        title:this.state.title,
        story:this.state.story
    })
    return  ToastAndroid.show("your story has been submitted",ToastAndroid.short)
    this.setState({
         author:'',
        title:'',
        story:''

    })
}
    render(){
        return(
            <View>
                        <Header/>
                        <KeyboardAvoidingView style={styles.keyBoardStyle}>
                          
        <TextInput
        title='title'
        style={styles.title}
        
        />
          <TextInput
        title='author'
        style={styles.author}
        
        />
  <TextInput
        title='story'
        style={styles.story}
        multiline={true}
        
        />
<TouchableOpacity style={styles.submit}
title='submit'
onPress={this.submitStory}

>
    
</TouchableOpacity>
        
        </KeyboardAvoidingView>
        </View>

        )
    }
}
const styles=StyleSheet.create({
  keyBoardStyle:{
      flex:2,
      alignItems:'centre',
      borderColor:'pink'
  },
    title:{
        border:3,
        borderColor:'blue',
        width:30,
        height:10
    },
author:{
    border:3,
    borderColor:'red',
    width:30,
    height:10
},
story:{
    border:3,
    borderColor:'pink',
    width:50,
    height:70
},
submit:{
    border:3,
    borderColor:'green',
    backgroundColor:'light Green',
    width:30,
    height:10
}

})
